/* Button.css */
@import 'tailwindcss/base';
@import 'tailwindcss/components';
@import 'tailwindcss/utilities';

.button-custom {
  @apply bg-green-500 text-white font-bold py-2 px-4 rounded;
}

.button-custom:hover {
  @apply bg-green-600;
}
const axios = require('axios');

// Replace 'BASE_URL' with the actual API endpoint
const BASE_URL = 'https://api.example.com';

// Create operation (POST)
const createData = async (data) => {
  try {
    const response = await axios.post(`${BASE_URL}/items`, data);
    return response.data;
  } catch (error) {
    console.error('Error creating data:', error);
  }
};

// Read operation (GET)
const readData = async (itemId) => {
  try {
    const response = await axios.get(`${BASE_URL}/items/${itemId}`);
    return response.data;
  } catch (error) {
    console.error('Error reading data:', error);
  }
};

// Update operation (PUT or PATCH)
const updateData = async (itemId, newData) => {
  try {
    const response = await axios.put(`${BASE_URL}/items/${itemId}`, newData);
    return response.data;
  } catch (error) {
    console.error('Error updating data:', error);
  }
};

// Delete operation (DELETE)
const deleteData = async (itemId) => {
  try {
    const response = await axios.delete(`${BASE_URL}/items/${itemId}`);
    return response.data;
  } catch (error) {
    console.error('Error deleting data:', error);
  }
};

// Example usage
(async () => {
  const newItem = await createData({ name: 'New Item' });
  console.log('Created Item:', newItem);

  const itemToRead = await readData(newItem.id);
  console.log('Read Item:', itemToRead);

  const updatedItem = await updateData(newItem.id, { name: 'Updated Item' });
  console.log('Updated Item:', updatedItem);

  const deletedItem = await deleteData(newItem.id);
  console.log('Deleted Item:', deletedItem);
})();
